/**
 * There are a few  tags with CSS resource in them that are preloaded in the page
 * in each of those there is a "onload" handler which invokes the loaddCSS callback
 * defined here.
 * We are monitoring 3 main CSS files - the runtime, the global and the page,
 * and once all of them are loaded we convert the  tag into a valid stylesheet type
 * which then appends the styles to the page.
 */
(function () {
  let cssLinks = {};
  function loadCssLink(link) {
    link.onload = null;
    link.rel = "stylesheet";
    link.type = "text/css";
  }

  function checkCss() {
    if (cssLinks && cssLinks.runtime && cssLinks.global) {
      loadCssLink(cssLinks.runtime);
      loadCssLink(cssLinks.global);
      cssLinks = null;
    }
  }

  function loadCSS(link) {
    try {
      var urlParams = new URLSearchParams(window.location.search);
      var noCSS = !!urlParams.get("nocss");
      var cssTimeout = urlParams.get("cssTimeout") || 0;

      if (noCSS) {
        return;
      }
      if (link.href.includes("d-css-runtime")) {
        cssLinks.runtime = link;
        checkCss();
      } else if (link.id === "siteGlobalCss") {
        cssLinks.global = link;
        checkCss();
      } else {
        requestIdleCallback(function () {
          window.setTimeout(function () {
            loadCssLink(link);
          }, parseInt(cssTimeout, 10));
        });
      }
    } catch (e) {
      /* Never fail - this is just a tool for measurements */
    }
  }
  window.loadCSS = window.loadCSS || loadCSS;
})();

